const cloudinary=require("cloudinary").v2,fs=require("fs");cloudinary.config({cloud_name:process.env.CLOUDINARY_CLOUD_NAME,api_key:process.env.CLOUDINARY_API_KEY,api_secret:process.env.CLOUDINARY_API_SECRET});const uploadOnCloudinary=async(filePath,folder)=>{if(!filePath)return null;if(!fs.existsSync(filePath))return null;try{const response=await cloudinary.uploader.upload(filePath,{resource_type:"auto",folder:folder});return fs.unlinkSync(filePath),response}catch(error){return fs.existsSync(filePath)&&fs.unlinkSync(filePath),null}};module.exports={uploadOnCloudinary:uploadOnCloudinary};